      // Act
      final result = await useCase(UpdateParams(id: '1', data: uProduct));

      // Assert
      verify(() => mockRepository.update(any())).called(1);
      expect(result.isSuccess, true);
      expect(result.getOrElse(() => throw Exception()), equals(tProduct));
